import React, { useState } from 'react';
import { 
  Box, 
  Button,
  CheckBox, 
  Collapsible,
  Heading, 
  Grommet,
  TextInput 
} from 'grommet';
import { 
  Code, 
  Edit,
  Menu,
  Secure,
  Tag 
} from 'grommet-icons';

const theme = {
  global: {
    drop:{
      background: '#444444',
      shadowSize: 'medium',
    },
    elevation: {
      dark: {
        medium: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
      },
      light: {
        medium: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
      },
    },
    colors: {
      brand: '#228BE6',
      test: '#757575',
    },
    font: {
      family: 'Arial',
      size: '20px',
      height: '20px'
    },
    input: {
      weight: 400,
    },
  },
  checkBox:{
    hover:{
      border:{
        color:{dark:'brand'},
      },
    },
    color:{
      dark: 'brand',
    },
    border:{
      color:{dark:'brand'},
    },
    toggle:{
      color:{
        light: 'brand',
      },
    },
  },
};

const AppBar = (props) => (
    <Box
      tag='header'
      direction='row'
      align='center'
      justify='between'
      background='brand'
      pad={{ left: 'medium', right: 'small', vertical: 'small' }}
      elevation='medium'
      style={{ zIndex: '1' }}
      {...props}
  />
);



const App = () => {
  const [showSettings, setShowSettings] = useState(false);
  const [showCustomURL, setShowCustomURL] = useState(false);
  const [isPrivate, setIsPrivate] = useState(false);
  const [showMaxReferences, setShowMaxReferences] = useState(false);
  const [showSidebar, setShowSidebar] = useState(false);

  return (
    <Grommet theme={theme} full>  
          <Box background='dark-1' fill>
            <AppBar>
              <Menu 
                color='lightgrey'
                onClick={() => setShowSidebar(!showSidebar)}
              />
            </AppBar>

            <Box flex direction='row' overflow={{ horizontal: 'hidden' }}>
              <Collapsible direction='horizontal' open={showSidebar}>
                <Box
                flex
                width='medium'
                background='light-2'
                elevation='small'
                align='center'
                justify='center'
                >
                  sidebar - hier kommen Infos zu PerVerSo rein
                </Box>
              </Collapsible>

              <Box
              flex
              align='center'
              justify='center'
              >

                <Heading level='1'>PerVerSo Shorter URLs</Heading>
                <Box
                  width="large"
                  direction="row"
                  align="center"
                  pad={{ horizontal: 'small', vertical: 'xsmall' }}
                  round="small"
                  border={{
                    side: 'all',
                    color: 'brand',
                  }}
                >
                  <Edit color='brand'/>
                  <TextInput 
                  plain
                  placeholder='Enter your URL...'
                  /> 
                </Box>

                <Box
                flex
                direction='row'
                align='start'
                >
                  <Button
                    label='Shorten'
                    color='brand'
                  />

                  <Button 
                    label='Options'
                    color='brand'
                    onClick={() => setShowSettings(!showSettings)}
                  />
                </Box>

                <Collapsible direction='vertical' open={showSettings}>
                  <Box
                  flex
                  direction='column'
                  align='center'
                  justify='center'
                  >
                    <Box
                      width="medium"
                      direction="row"
                      align="start"
                    >
                      <CheckBox
                        toggle
                        label='Custom tag'
                        onClick={() => setShowCustomURL(!showCustomURL)}
                      />
                    </Box>
                    <Box
                      width="medium"
                      direction="row"
                      align="start"
                    >
                      <CheckBox
                        toggle
                        label='Set max. references'
                        onClick={() => setShowMaxReferences(!showMaxReferences)}
                      />
                    </Box>
                    <Box
                      width="medium"
                      direction="row"
                      align="start"
                    >
                      <CheckBox
                        toggle
                        label='Is private'
                        onClick={() => setIsPrivate(!isPrivate)}
                      />
                      <Secure color='brand'/>
                    </Box>

                    <Code color='brand'/>

                    <Tag color='brand'/>
                  </Box>
                </Collapsible>

              </Box>
            </Box>

          </Box>
    </Grommet>
  );
}


export default App;
