import React, { useState} from 'react';
import { 
  Box, 
  Button,
  CheckBox, 
  Heading, 
  Grommet,
  TextInput 
} from 'grommet';
import { Notification, Search } from 'grommet-icons';

const theme = {
  global: {
    drop:{
      background: '#444444',
      shadowSize: 'medium',
      extend: `
        border-bottom-left-radius: 12px;
        border-bottom-right-radius: 12px;
        overflow: hidden;
      `,
    },
    elevation: {
      dark: {
        medium: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
      },
      light: {
        medium: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
      },
    },
    colors: {
      brand: '#228BE6',
    },
    font: {
      family: 'Arial',
      size: '20px',
      height: '20px'
    },
    input: {
      weight: 400,
    },
  },
};

const AppBar = (props) => (
  <Box
    tag='header'
    direction='row'
    align='center'
    justify='between'
    background='brand'
    pad={{left: 'medium', right: 'small', vertical: 'small'}}
    elevation='medium'
    style={{ zIndex: '1' }}
    {...props}
  />
);

const App = () => {
  const [showSettings, setShowSettings] = useState(false);

  const SimpleCheckBox = ({ checked: checkedProp, ...rest }) => {
    const [checked, setChecked] = useState(!!checkedProp);
    const onChange = event => setChecked(event.target.checked);
  return (
    <Grommet theme={theme} full>  
          <Box background='dark-1' fill>
            <AppBar>
              <Heading level='3' margin='none'>My App</Heading>
              <Button 
                icon={<Notification />}
                onClick={() => setShowSidebar(!showSidebar)} 
              />
            </AppBar>
              <Box flex align='center' justify='center'>
                <Box
                  width='large'
                  direction='row'
                  align='center'
                  pad={{horizontal:'small', vertical:'xsmall'}}
                  round='small'
                  border={{
                    side: 'all',
                    color: 'brand',
                  }}
                >
                  <Search color='brand'/>
                  <TextInput
                    placeholder='Enter your url...'
                    plain
                  />
                </Box>
                {showSettings && (
                  <Box flex direction='row' align='center' justify='center'>
                    <CheckBox {...rest} checked={checked} onChange={onChange} />
                  </Box>
                )}
                <Box flex direction='row' align='center' justify='center'>
                  <Button 
                    label="Shorten"
                    color='brand'
                    onClick={() => {}} 
                  />
                  <Button 
                    label="Options"
                    color='brand'
                    onClick={() => setShowSettings(!showSettings)} 
                  />
                </Box>
              </Box>

            </Box>
    </Grommet>
    );
  };
}

export default App;
