import React, { useState} from 'react';
import { 
  Box, 
  Button,
  CheckBox, 
  Heading, 
  Grommet,
  TextInput 
} from 'grommet';
import { Code } from 'grommet-icons';

const theme = {
  global: {
    drop:{
      background: '#444444',
      shadowSize: 'medium',
    },
    elevation: {
      dark: {
        medium: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
      },
      light: {
        medium: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
      },
    },
    colors: {
      brand: '#228BE6',
      test: '#757575',
    },
    font: {
      family: 'Arial',
      size: '20px',
      height: '20px'
    },
    input: {
      weight: 400,
    },
  },
  checkBox:{
    hover:{
      border:{
        color:{dark:'brand'},
      },
    },
    color:{
      dark: 'brand',
    },
    border:{
      color:{dark:'brand'},
    },
    toggle:{
      color:{
        light: 'brand',
      },
    },
  },
};

const AppBar = (props) => (
  <Box
    tag='header'
    direction='row'
    align='center'
    justify='between'
    background='brand'
    pad={{left: 'medium', right: 'small', vertical: 'small'}}
    elevation='medium'
    style={{ zIndex: '1' }}
    {...props}
  />
);



const App = () => {
  const [showSettings, setShowSettings] = useState(false);


  return (
    <Grommet theme={theme} full>  
          <Box background='dark-1' fill>
            <AppBar>
              <Heading level='3' margin='none'>PerVerSo Shorter URLs</Heading>
            </AppBar>
              <Box flex align='center' justify='center' basis='xsmall'>
                <Box
                  width='large'
                  direction='row'
                  align='center'
                  pad={{horizontal:'small', vertical:'xsmall'}}
                  round='small'
                  border={{
                    side: 'all',
                    color: 'brand',
                  }}
                >
                  <Code color='brand'/>
                  <TextInput
                    placeholder='Shorten your url...'
                    plain
                  />
                </Box>
              </Box>
              <Box flex direction='column' align='center' justify='center'>
                <Box 
                  flex direction='row' 
                  align='start' 
                  justify='center'
                  >
                  <Button 
                    label="Shorten"
                    color='brand'
                    onClick={() => {}} 
                  />
                  <Button 
                    label="Options"
                    color='brand'
                    onClick={() => setShowSettings(!showSettings)} 
                  />
                </Box>
                {showSettings && (
                  <Box flex direction='column' justify='start' align='start'>
                    <CheckBox 
                      label='Use custom url' 
                      toggle
                    />
                    <CheckBox label='Add max number of references' toggle/>
                    <CheckBox label='Is private' toggle/>
                  </Box>
                )}
              </Box>

            </Box>
    </Grommet>
  );
}


export default App;
