import React, { useState } from 'react';
import { 
  Box, 
  Button,
  CheckBox, 
  Collapsible,
  Heading, 
  Grommet,
  TextInput 
} from 'grommet';
import { 
  Code, 
  Edit,
  Menu,
  Secure,
  Tag 
} from 'grommet-icons';

const theme = {
  global: {
    drop:{
      background: '#444444',
      shadowSize: 'medium',
    },
    elevation: {
      dark: {
        medium: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
      },
      light: {
        medium: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
      },
    },
    colors: {
      brand: '#228BE6',
      test: '#757575',
    },
    font: {
      family: 'Arial',
      size: '20px',
      height: '20px'
    },
    input: {
      weight: 400,
    },
  },
  checkBox:{
    hover:{
      border:{
        color:{dark:'brand'},
      },
    },
    color:{
      dark: 'brand',
    },
    border:{
      color:{dark:'brand'},
    },
    toggle:{
      color:{
        light: 'brand',
      },
    },
  },
};

const AppBar = (props) => (
    <Box
      tag='header'
      direction='row'
      align='center'
      justify='between'
      background='brand'
      pad={{ left: 'medium', right: 'small', vertical: 'small' }}
      elevation='medium'
      style={{ zIndex: '1' }}
      {...props}
  />
);



const App = () => {
  const [showSettings, setShowSettings] = useState(false);
  const [showCustomURL, setShowCustomURL] = useState(false);
  const [isPrivate, setIsPrivate] = useState(false);
  const [showMaxReferences, setShowMaxReferences] = useState(false);
  const [showSidebar, setShowSidebar] = useState(false);

  return (
    <Grommet theme={theme} full>  
          <Box background='dark-1' fill>
            <AppBar>
              <Menu 
                color='lightgrey'
                onClick={() => setShowSidebar(!showSidebar)}
              />
            </AppBar>

            <Box flex direction='row' overflow={{ horizontal: 'hidden' }} gap='medium'>
              <Collapsible direction='horizontal' open={showSidebar}>
                <Box
                flex
                width='medium'
                background='light-2'
                elevation='small'
                align='center'
                justify='center'
                >
                  sidebar - hier kommen Infos zu PerVerSo rein
                </Box>
              </Collapsible>

              <Box
              flex
              align='center'
              justify='center'
              gap='medium'
              >

                <Heading level='1'>PerVerSo Shorter URLs</Heading>
                <Box
                  width="large"
                  direction="row"
                  align="center"
                  pad={{ horizontal: 'small', vertical: 'xsmall' }}
                  round="small"
                  border={{
                    side: 'all',
                    color: 'brand',
                  }}
                >
                  <Code color='brand'/>
                  <TextInput 
                  plain
                  placeholder='Enter your URL...'
                  /> 
                </Box>

                <Box
                gap='large'
                direction='row'
                align='center'
                justify='center'
                >
                  <Button
                    label='Shorten'
                    color='brand'
                  />

                  <Button 
                    label='Options'
                    color='brand'
                    onClick={() => setShowSettings(!showSettings)}
                  />
                </Box>

                <Collapsible direction='vertical' open={showSettings}>
                  <Box
                  flex
                  direction='column'
                  align='center'
                  justify='center'
                  gap='medium'
                  >
                    <Box
                      width="large"
                      direction="row"
                      align="center"
                      gap='medium'
                    >
                      <CheckBox
                        toggle
                        label='Custom tag'
                        onClick={() => setShowCustomURL(!showCustomURL)}
                      />
                      <Collapsible direction='horizontal' open={showCustomURL}>
                        <Box width='large' direction='row' justify='end' background={{ color: 'brand', opacity: true }}>
                          <Box
                            width="medium"
                            direction="row"
                            align="center"
                            justify='end'
                            pad={{ horizontal: 'small', vertical: 'xsmall' }}
                            round="small"
                            border={{
                              side: 'all',
                              color: 'brand',
                            }}
                          >
                            <Tag color='brand'/>
                            <TextInput 
                            plain
                            placeholder='Enter custom tag...'
                            /> 
                          </Box>
                        </Box>
                      </Collapsible>
                    </Box>
                    <Box
                      width="large"
                      direction="row"
                      align="center"
                      gap='medium'
                    >
                      <CheckBox
                        toggle
                        label='Set max. references'
                        onClick={() => setShowMaxReferences(!showMaxReferences)}
                      />
                      <Collapsible direction='horizontal' open={showMaxReferences}>
                        <Box
                          width="medium"
                          direction="row"
                          align="center"
                          justify='end'
                          pad={{ horizontal: 'small', vertical: 'xsmall' }}
                          round="small"
                          border={{
                            side: 'all',
                            color: 'brand',
                          }}
                        >
                          <Edit color='brand'/>
                          <TextInput 
                          plain
                          placeholder='Enter max. References...'
                          /> 
                        </Box>
                      </Collapsible>
                    </Box>
                    <Box
                      width="large"
                      direction="row"
                      align="start"
                      gap='small'
                    >
                      <CheckBox
                        toggle
                        label='Is private'
                        onClick={() => setIsPrivate(!isPrivate)}
                      />
                      <Collapsible direction='horizontal' open={isPrivate}>
                        <Secure color='brand'/>
                      </Collapsible>
                    </Box>

                  </Box>
                </Collapsible>

              </Box>
            </Box>

          </Box>
    </Grommet>
  );
}


export default App;
