import React, { useState } from 'react';
import { 
  Box,
  Grommet
} from 'grommet';

import home from './sites/home'
import notFound from './sites/notFound'
import safeLoad from './sites/safeLoad'

import MainContent from './components/MainContent'
import Sidebar from './components/Sidebar'
import AppBar from './components/AppBar'
import Help from './components/Help'
import AppFooter from './components/AppFooter'

import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';

import { pvsTheme } from './theme/theme'



const App = () => {
  const [showStatistics, setShowStatistics] = useState(false);
  const [statistics, setStatistics] = useState([]);
  const [showSidebar, setShowSidebar] = useState(false);
  const [showHelp, setShowHelp] = useState(false);

  return (
        <Grommet theme={ pvsTheme } full>  
          <Box background='dark-1' fill>
            <AppBar 
              setShowSidebar={setShowSidebar}
              showSidebar={showSidebar}
              setShowHelp={setShowHelp}
              showHelp={showHelp}
            />

            <Box flex direction='row' overflow={{ horizontal: 'hidden' }} gap='medium'>
              <Sidebar 
                showSidebar={showSidebar}
                showStatistics={showStatistics}
                setShowStatistics={setShowStatistics}
                setStatistics={setStatistics}
                statistics={statistics}
              />

              <Router>
                <Switch>
                  <Route path="/" exact component={MainContent} />
                  <Route path="/404" component={notFound}/>
                  <Route path="/:url/safe" component={safeLoad}/>
                </Switch>

              </Router>

            </Box>

            <Help 
              setShowHelp={setShowHelp}
              showHelp={showHelp}
            />



          </Box>
          <AppFooter/>
    </Grommet>
  );
}


export default App;
