import React, { useState } from 'react';
import { 
  Box, 
  Button,
  Clock,
  Image,
  Footer,
  Grommet,
  Text
} from 'grommet';
import { 
  FormClose,
  Menu
} from 'grommet-icons';
import AppBar from './components/AppBar'
import Sidebar from './components/Sidebar'
import MainContent from './components/MainContent'
import Help from './components/Help'

const theme = {
  global: {
    drop:{
      background: '#444444',
      shadowSize: 'medium',
    },
    elevation: {
      dark: {
        medium: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
      },
      light: {
        medium: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
      },
    },
    colors: {
      brand: '#6496FA',
      test: 'red',
      text: {
        dark: 'lightgrey',
      },
    },
    font: {
      family: 'Arial',
      size: '20px',
      height: '20px'
    },
    input: {
      weight: 400,
    },
  },
  checkBox:{
    hover:{
      border:{
        color:{dark:'brand'},
      },
    },
    color:{
      dark: 'brand',
    },
    border:{
      color:{dark:'brand'},
    },
    toggle:{
      color:{
        light: 'brand',
      },
    },
  },
  card: {
    container: {
      background: {dark:'#FFFFFF12'},
      elevation: 'none',
    },
    footer: {
      background: '#FFFFFF06',
    },
  },
  button: {
    border:{
      radius: '12px',
    },
    default: {
      background:{
        color: {dark:'brand'},
      },
      color:{dark:'lightgrey', light: 'lightgrey'},
    },

  },
};





const App = () => {

  const domain = 'http://localhost:5000'
  const [showStatistics, setShowStatistics] = useState(false);
  const [statistics, setStatistics] = useState([]);
  const [showSidebar, setShowSidebar] = useState(false);

  const [showHelp, setShowHelp] = useState(false);


  return (
    <Grommet theme={theme} full>  
          <Box background='dark-1' fill>
            <AppBar>
              <Button 
                icon={<Menu color='lightgrey'/>}
                onClick={() => setShowSidebar(!showSidebar)}
              />
              <Button 
                label='Help'
                onClick={() => setShowHelp(!showHelp)}
              />

            </AppBar>

            <Box flex direction='row' overflow={{ horizontal: 'hidden' }} gap='medium'>
              <Sidebar 
                showSidebar={showSidebar}
                showStatistics={showStatistics}
                setShowStatistics={setShowStatistics}
                statistics={statistics}
                domain={domain}
              />

              <MainContent
                domain={domain}
              />

            </Box>

            <Help 
              setShowHelp={setShowHelp}
              showHelp={showHelp}
            />


          </Box>
          <Footer background="brand" justify="center" pad="medium">
            <Clock type='digital' color={{dark:'lightgrey'}}/>
            <Text textAlign="center" size="medium" color='lightgrey'>
              © 2021 Copyright PerVerSo
            </Text>
            <Box
            direction='row'
            justify='end'
            >
              <Image src='../../PerVerSo32x32.png'/>
            </Box>
          </Footer>
    </Grommet>
  );
}


export default App;
