import React, { useState} from 'react';
import { 
  Box, 
  Button, 
  Heading, 
  Grommet,
  ResponsiveContext 
} from 'grommet';
import { FormClose, Notification, Search } from 'grommet-icons';

const theme = {
  global: {
    colors: {
      brand: '#228BE6',
    },
    font: {
      family: 'Roboto',
      size: '18px',
      height: '20px'
    },
  },
};

const AppBar = (props) => (
  <Box
    tag='header'
    direction='row'
    align='center'
    justify='between'
    background='brand'
    pad={{left: 'medium', right: 'small', vertical: 'small'}}
    elevation='medium'
    style={{ zIndex: '1' }}
    {...props}
  />
);

const App = () => {
  const [showSidebar, setShowSidebar] = useState(false);
  return (
    <Grommet theme={theme} full>
      <ResponsiveContext.Consumer>    
          <Box fill>
            <AppBar>
              <Heading level='3' margin='none'>My App</Heading>
              <Button 
                icon={<Notification />}
                onClick={() => setShowSidebar(!showSidebar)} 
              />
            </AppBar>
              <Box flex align='center' justify='center'>
                app body
              </Box>

            </Box>
      </ResponsiveContext.Consumer>
    </Grommet>
  );
}

export default App;
