import React, { useState} from 'react';
import { 
  Box, 
  Button,
  CheckBox, 
  Collapsible,
  Heading, 
  Grommet,
  TextInput 
} from 'grommet';
import { 
  Code, 
  Edit,
  Menu,
  Secure,
  Tag 
} from 'grommet-icons';

const theme = {
  global: {
    drop:{
      background: '#444444',
      shadowSize: 'medium',
    },
    elevation: {
      dark: {
        medium: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
      },
      light: {
        medium: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
      },
    },
    colors: {
      brand: '#228BE6',
      test: '#757575',
    },
    font: {
      family: 'Arial',
      size: '20px',
      height: '20px'
    },
    input: {
      weight: 400,
    },
  },
  checkBox:{
    hover:{
      border:{
        color:{dark:'brand'},
      },
    },
    color:{
      dark: 'brand',
    },
    border:{
      color:{dark:'brand'},
    },
    toggle:{
      color:{
        light: 'brand',
      },
    },
  },
};

const AppBar = (props) => (
    <Box
      tag='header'
      direction='row'
      align='center'
      justify='between'
      background='brand'
      pad={{ left: 'medium', right: 'small', vertical: 'small' }}
      elevation='medium'
      style={{ zIndex: '1' }}
      {...props}
  />
);



const App = () => {
  const [showSettings, setShowSettings] = useState(false);
  const [showCustomURL, setShowCustomURL] = useState(false);
  const [isPrivate, setIsPrivate] = useState(false);
  const [showMaxReferences, setShowMaxReferences] = useState(false);
  const [showSidebar, setShowSidebar] = useState(false);

  return (
    <Grommet theme={theme} full>  
          <Box background='dark-1' fill>
            <AppBar>
              <Menu 
                color='lightgrey'
                onClick={() => setShowSidebar(!showSidebar)}
              />
            </AppBar>
            <Box>

            </Box>
            <Box flex direction='row' overflow={{ horizontal: 'hidden' }}>
              <Box 
                flex 
                align='center' 
                justify='center' 
                margin='none'
              >
                <Box 
                  flex 
                  direction='row'  
                  justify='center'
                  align='end' 
                  basis='xxsmall'
                  width='large'
                >
                  <Heading level='1' margin='none' color='lightgrey'>PerVerSo Shorter URLs</Heading>
                </Box>
                <Box
                  width='large'
                  direction='row'
                  align='center'
                  pad={{horizontal:'small', vertical:'xsmall'}}
                  round='small'
                  border={{
                    side: 'all',
                    color: 'brand',
                  }}
                  margin='large'
                >
                  <Code color='brand'/>
                  <TextInput
                    placeholder='Shorten your url...'
                    plain
                  />
                </Box>
              </Box>
              <Box flex direction='column' align='center' justify='center'>
                <Box 
                  flex direction='row' 
                  align='start' 
                  justify='center'
                  >
                  <Button 
                    label="Shorten"
                    color='brand'
                    onClick={() => {}} 
                  />
                  <Button 
                    label="Options"
                    color='brand'
                    onClick={() => setShowSettings(!showSettings)} 
                  />
                </Box>



                <Collapsible direction='vertical' open={showSettings}>
                  <Box flex direction='column' justify='start' align='start'>
                    <Box flex direction='row' justify='start' align='center'>
                      <CheckBox 
                        label='Use custom url' 
                        toggle
                        onChange={() => setShowCustomURL(!showCustomURL)}
                      />
                      <Collapsible direction='horizontal' open={showCustomURL}>
                        <Box
                          width='medium'
                          direction='row'
                          align='center'
                          pad={{horizontal:'small', vertical:'xsmall'}}
                          round='small'
                          border={{
                            side: 'all',
                            color: 'brand',
                          }}
                          margin='large'
                        >
                          <Tag color='brand'/>
                          <TextInput
                            placeholder='Your custom tag...'
                            plain
                          />
                        </Box>
                        </Collapsible>
                    </Box>
                    <Box flex direction='row' justify='start' align='center'>
                      <CheckBox 
                        label='Add max number of references' 
                        toggle
                        onChange={()=>setShowMaxReferences(!showMaxReferences)}
                        />
                      <Collapsible direction='horizontal' open={showMaxReferences}>
                        <Box
                          width='medium'
                          direction='row'
                          align='center'
                          pad={{horizontal:'small', vertical:'xsmall'}}
                          round='small'
                          border={{
                            side: 'all',
                            color: 'brand',
                          }}
                          margin='large'
                        >
                          <Edit color='brand'/>
                          <TextInput
                            placeholder='Your max references...'
                            plain
                          />
                        </Box>
                      </Collapsible>
                    </Box>
                    <Box flex direction='row' justify='start' align='center'>
                      <CheckBox 
                        label='Is private' 
                        toggle
                        onChange={() => setIsPrivate(!isPrivate)}
                        />
                      <Collapsible direction='horizontal' open={isPrivate}>
                        <Secure color='brand'/>
                      </Collapsible>
                    </Box>
                  </Box>
                </Collapsible>



                <Box flex direction='row' justify='center' align='center'>
                <Box flex direction='column' justify='start' align='center'>
                  <Heading level='3' margin='none'>Top 10 Shortened URLs</Heading>
                </Box>
                <Box flex direction='column' justify='start' align='center'>
                  <Heading level='3' margin='none'>Werbung</Heading>
                </Box>

              </Box>
            </Box>
            </Box>

            </Box>
    </Grommet>
  );
}


export default App;
