import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Button,
  Card,
  CardBody,
  CardFooter,
  CheckBox, 
  Collapsible,
  Layer,
  Footer,
  Heading, 
  Grommet,
  Select,
  Text,
  TextInput
} from 'grommet';
import { 
  Cart,
  Code, 
  Configure,
  Edit,
  FormClose,
  Info,
  Menu,
  Secure,
  Tag 
} from 'grommet-icons';

const theme = {
  global: {
    drop:{
      background: '#444444',
      shadowSize: 'medium',
    },
    elevation: {
      dark: {
        medium: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
      },
      light: {
        medium: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
      },
    },
    colors: {
      brand: '#6496FA',
      test: '#757575',
      text: {
        dark: 'lightgrey',
      },
    },
    font: {
      family: 'Arial',
      size: '20px',
      height: '20px'
    },
    input: {
      weight: 400,
    },
  },
  checkBox:{
    hover:{
      border:{
        color:{dark:'brand'},
      },
    },
    color:{
      dark: 'brand',
    },
    border:{
      color:{dark:'brand'},
    },
    toggle:{
      color:{
        light: 'brand',
      },
    },
  },
  card: {
    container: {
      background: {dark:'#FFFFFF12'},
      elevation: 'none',
    },
    footer: {
      background: '#FFFFFF06',
    },
  },
  button: {
    border:{
      radius: '12px',
    },
    default: {
      background:{
        color: {dark:'brand'},
      },
      color:{dark:'lightgrey', light: 'lightgrey'},
    },

  },
};

const AppBar = (props) => (
    <Box
      tag='header'
      direction='row'
      align='center'
      justify='between'
      background='brand'
      pad={{ left: 'medium', right: 'small', vertical: 'small' }}
      elevation='medium'
      style={{ zIndex: '1' }}
      {...props}
  />
);



const App = () => {
  const [showSettings, setShowSettings] = useState(false);
  const [showCustomURL, setShowCustomURL] = useState(false);
  const [isPrivate, setIsPrivate] = useState(false);
  const [showMaxReferences, setShowMaxReferences] = useState(false);
  const [showSidebar, setShowSidebar] = useState(false);
  const [showWissen, setShowWissen] = useState(false);
  const [showProducts, setShowProducts] = useState(false);
  const [showRandomizer, setShowRandomizer] = useState(false);
  const [CustomURL, setCustomURL] = useState('');
  const [MaxReferences, setMaxReferences] = useState(-1);
  const [Randomizer, setRandomizer] = useState(1);
  const [URLInput, setURLInput] = useState('');
  const [URL, setURL] = useState('');
  const [showResult, setShowResult] = useState(false);
  const [message, setMessage] = useState("");

  const randomOptions = [
    { label: 'Default', value: 1 },
    { label: 'Uppercase', value: 2 },
    { label: 'Lowercase', value: 3 },
    { label: 'Numbers', value: 4 },
  ];

  const createShortUrl = async () => {
    var base = `https://localhost:5000/api/create?url=${URL}`;
    if(showCustomURL){
      base= base + `&custom=${CustomURL}`;
    }
    if(isPrivate){
      base= base + "&private=1";
    }
    if(showMaxReferences){
      base=base + `&refs_available=${MaxReferences}`;
    }
    base = base + `&random_method=${Randomizer}`
    const response = await fetch(base);
    const dataUrl = await response.json();
    message= setMessage(dataUrl.message);
  };

  useEffect(() => {
    createShortUrl();
  }, [URL]);

  const updateURL = e => {
    setURLInput(e.target.value);
  }
  const updateMaxRef = e => {
    setMaxReferences(e.target.value);
  }
  const updateCustomURL = e => {
    setCustomURL(e.target.value);
  }

  const getSearch = e => {
    e.preventDefault();
    setURL(URLInput);
  }

  return (
    <Grommet theme={theme} full>  
          <Box background='dark-1' fill>
            <AppBar>
              <Button 
                icon={<Menu color='lightgrey'/>}
                onClick={() => setShowSidebar(!showSidebar)}
              />
              <Button 
                label='Help'
                onClick={() => setShowSidebar(!showSidebar)}
              />

            </AppBar>

            <Box flex direction='row' overflow={{ horizontal: 'hidden' }} gap='medium'>
              <Collapsible direction='horizontal' open={showSidebar}>
                <Box
                flex
                width='medium'
                background='dark'
                elevation='small'
                align='center'
                justify='start'
                gap='medium'
                >
                  <Heading level='2'>Wissenswertes</Heading>
                  <Card onClick={()=>setShowWissen(!showWissen)}>
                    <CardBody>
                      <Box flex direction='row'>
                        <Box justify='center' align='center' pad='medium'>
                          <Info color='brand' size='large'/>
                        </Box>
                        <Box justify='center' align="start" direction="column" pad="large" >
                          <Text size='medium' weight="bold">
                            PerVerSo Historie
                          </Text>
                          <Text size='medium'>Wie alles begann</Text>
                        </Box>
                      </Box>

                    </CardBody>
                    <CardFooter background={{dark:'#FFFFFF06'}}>
                      <Text size="medium">Mehr Informationen..</Text>
                    </CardFooter>
                  </Card>


                  <Card onClick={()=>setShowProducts(!showProducts)}>
                    <CardBody>
                    <Box flex direction='row'>
                        <Box justify='center' align='center' pad='medium'>
                          <Cart color='brand' size='large'/>
                        </Box>
                        <Box justify='center' align="start" direction="column" pad="large" >
                          <Text size='medium' weight="bold">
                            PerVerSo Produkte
                          </Text>
                          <Text size='medium'>Produktportfolio</Text>
                        </Box>
                      </Box>
                    </CardBody>
                    <CardFooter background={{dark:'#FFFFFF06'}}>
                      <Text size="medium">Mehr Informationen..</Text>
                    </CardFooter>
                  </Card>

                  <Collapsible open={showWissen}>
                    <Layer>
                      <Box>
                          <Button
                          icon={<FormClose color='lightgrey'/>}
                          onClick={()=>setShowWissen(false)}
                          />
                        </Box>
                        <Box pad='full' justify='center' gap='medium' margin='medium'>
                          <Heading level='2'>Entstehung</Heading>
                          <Text size='medium'>
                            PerVerSo war ursprünglich ein studentisches Softwareprojekt für eine Personalverwaltungssoftware. Im Laufe der Zeit kamen weitere Projekte hinzu, sodass sich PerVerSo nach und nach zu einer Marke etabliert hat.
                            Für die Entwicklergruppe steht dabei stets der Nutzende im Vordergrund, wobei ein gewisser Humor nicht verloren gegangen ist.
                          </Text>
                        </Box>

                    </Layer>
                  </Collapsible>


                  <Collapsible open={showProducts}>
                    <Layer>  
                      <Box>
                        <Button
                        icon={<FormClose color='lightgrey'/>}
                        onClick={()=>setShowProducts(false)}
                        />
                      </Box>
                      <Box pad='full' align='center' justify='center' gap='medium' margin='medium'>
                        <Heading level='2'>Produkte von PerVerSo</Heading>
                      <Card>
                        <CardBody>
                        <Box gap="small" align="center" direction="row" pad="large" width='large'>
                          <Box>
                            <Text size='large' weight="bold">
                              PerVerSo
                            </Text>
                            <Text size='medium'>Personalverwaltungssoftware, basierend auf Java</Text>
                          </Box>
                        </Box>
                        </CardBody>
                      </Card>
                      <Card>
                        <CardBody>
                        <Box gap="small" align="center" direction="row" pad="large" width='large'>
                          <Box>
                            <Text size='large' weight="bold">
                              PerVerSo MSO
                            </Text>
                            <Text size='medium'>Personalverwaltungssoftware, implementiert in Microsoft Excel</Text>
                          </Box>
                        </Box>
                        </CardBody>
                      </Card>
                      <Card>
                        <CardBody>
                        <Box gap="small" align="center" direction="row" pad="large" width='large'>
                          <Box>
                            <Text size='large' weight="bold">
                              PerVerSo MSO AEP
                            </Text>
                            <Text size='medium'>Personalverwaltungssoftware, in Microsoft Excel zur Einsatzplanung von Auszubildenden</Text>
                          </Box>
                        </Box>
                        </CardBody>
                      </Card>
                      <Card>
                        <CardBody>
                        <Box gap="small" align="center" direction="row" pad="large" width='large'>
                          <Box>
                            <Text size='large' weight="bold">
                              Weatherplans
                            </Text>
                            <Text size='medium'>Wettervorhersage zum Testen von APIs</Text>
                          </Box>
                        </Box>
                        </CardBody>
                      </Card>
                      <Card>
                        <CardBody>
                        <Box gap="small" align="center" direction="row" pad="large" width='large'> 
                          <Box>
                            <Text size='large' weight="bold">
                              OLE (Kooperation)
                            </Text>
                            <Text size='medium'>Der Online LaTeX Editor mit kollaborativen Möglichkeiten zum Selfhosting</Text>
                          </Box>
                        </Box>
                        </CardBody>
                      </Card>
                      </Box>
                    </Layer> 
                  </Collapsible>


                </Box>
              </Collapsible>

              <Box
              flex
              align='center'
              justify='center'
              gap='medium'
              >

                <Heading level='1'>PerVerSo Shorter URLs</Heading>
                <Box direction='row' gap='small'>
                <Box
                    width="xsmall"
                    direction="row"
                    align="center"
                    justify='center'
                    pad={{ horizontal: 'small', vertical: 'xsmall' }}
                    round="small"
                    background='brand'
                    border={{
                      side: 'all',
                      color: 'brand',
                    }}
                  >
                    <Button 
                      icon={<Configure color='lightgrey'/>}
                      color='brand'
                      onClick={() => setShowSettings(!showSettings)}
                    />
                </Box>
                <Box
                  width="large"
                  direction="row"
                  align="center"
                  pad={{ horizontal: 'small', vertical: 'xsmall' }}
                  round="small"
                  border={{
                    side: 'all',
                    color: 'brand',
                  }}
                >
                  <Code color='brand'/>
                  <TextInput 
                  plain
                  placeholder='Enter your URL...'
                  onChange={updateURL}
                  /> 
                </Box>


                  <Button
                    label='Shorten'
                    color='brand'
                    type='submit'
                    onSubmit={getSearch}
                  />

                </Box>

                <Collapsible direction='vertical' open={showResult}>
                  <Box
                  width='large'
                  >
                    <Text size='medium'>{message}</Text>
                  </Box>
                </Collapsible>

                <Collapsible direction='vertical' open={showSettings}>
                  <Box
                  flex
                  direction='column'
                  align='center'
                  justify='center'
                  gap='medium'
                  >
                    <Box
                      width="large"
                      direction="row"
                      align="center"
                      gap='medium'
                    >
                      <CheckBox
                        toggle
                        label='Custom tag'
                        onClick={() => setShowCustomURL(!showCustomURL)}
                      />
                      <Collapsible direction='horizontal' open={showCustomURL}>
                        <Box width='large' direction='row' justify='end'>
                          <Box
                            width="medium"
                            direction="row"
                            align="center"
                            justify='end'
                            pad={{ horizontal: 'small', vertical: 'xsmall' }}
                            round="small"
                            border={{
                              side: 'all',
                              color: 'brand',
                            }}
                          >
                            <Tag color='brand'/>
                            <TextInput 
                            plain
                            placeholder='Enter custom tag...'
                            onChange={updateCustomURL}
                            /> 
                          </Box>
                        </Box>
                      </Collapsible>
                    </Box>
                    <Box
                      width="large"
                      direction="row"
                      align="center"
                      gap='medium'
                    >
                      <CheckBox
                        toggle
                        label='Set max. references'
                        onClick={() => setShowMaxReferences(!showMaxReferences)}
                      />
                      <Collapsible direction='horizontal' open={showMaxReferences}>
                        <Box width='large' direction='row' justify='end'>
                          <Box
                            width="medium"
                            direction="row"
                            align="center"
                            justify='end'
                            pad={{ horizontal: 'small', vertical: 'xsmall' }}
                            round="small"
                            border={{
                              side: 'all',
                              color: 'brand',
                            }}
                          >
                            <Edit color='brand'/>
                            <TextInput 
                            plain
                            placeholder='Enter max. References...'
                            onChange={updateMaxRef}
                            /> 
                          </Box>
                        </Box>
                      </Collapsible>
                    </Box>
                    <Box
                      width="large"
                      direction="row"
                      align="start"
                      gap='small'
                    >
                      <CheckBox
                        toggle
                        label='Is private'
                        onClick={() => setIsPrivate(!isPrivate)}
                      />
                      <Collapsible direction='horizontal' open={isPrivate}>
                        <Secure color='brand'/>
                      </Collapsible>
                    </Box>
                    <Box
                      width="large"
                      direction="row"
                      align="center"
                      gap='medium'
                    >
                      <CheckBox
                        toggle
                        label='Special randomizer'
                        onClick={() => setShowRandomizer(!showRandomizer)}
                      />
                      <Collapsible direction='horizontal' open={showRandomizer}>
                        <Box width='large' direction='row' justify='end'>
                          <Box
                            width="medium"
                            direction="row"
                            align="center"
                            justify='end'
                            pad={{ horizontal: 'small', vertical: 'xsmall' }}
                            round="small"
                            border={{
                              side: 'all',
                              color: 'brand',
                            }}
                          >
                            <Tag color='brand'/>
                            <Select 
                            placeholder='Select randomizer...'
                            options={randomOptions}
                            onChange={({ option }) => setRandomizer(option)}
                            size='small'
                            plain
                            />
                          </Box>
                        </Box>
                      </Collapsible>
                    </Box>

                  </Box>
                </Collapsible>

              </Box>
            </Box>

          </Box>
          <Footer background="brand" justify="center" pad="medium">
            <Text textAlign="center" size="medium">
