sourcesContent:
import React, { useEffect, useState } from 'react';
import { 
  Box, 
  Button,
  Card,
  CardBody,
  CardFooter,
  CheckBox, 
  Collapsible,
  Image,
  Layer,
  Footer,
  Heading, 
  Grommet,
  Select,
  Text,
  TextInput,
  Form
} from 'grommet';
import { 
  Cart,
  Code, 
  Configure,
  Edit,
  FormClose,
  Info,
  Menu,
  Secure,
  Tag 
} from 'grommet-icons';

const theme = {
  global: {
    drop:{
      background: '#444444',
      shadowSize: 'medium',
    },
    elevation: {
      dark: {
        medium: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
      },
      light: {
        medium: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
      },
    },
    colors: {
      brand: '#6496FA',
      test: '#757575',
      text: {
        dark: 'lightgrey',
      },
    },
    font: {
      family: 'Arial',
      size: '20px',
      height: '20px'
    },
    input: {
      weight: 400,
    },
  },
  checkBox:{
    hover:{
      border:{
        color:{dark:'brand'},
      },
    },
    color:{
      dark: 'brand',
    },
    border:{
      color:{dark:'brand'},
    },
    toggle:{
      color:{
        light: 'brand',
      },
    },
  },
  card: {
    container: {
      background: {dark:'#FFFFFF12'},
      elevation: 'none',
    },
    footer: {
      background: '#FFFFFF06',
    },
  },
  button: {
    border:{
      radius: '12px',
    },
    default: {
      background:{
        color: {dark:'brand'},
      },
      color:{dark:'lightgrey', light: 'lightgrey'},
    },

  },
};

const AppBar = (props) => (
    <Box
      tag='header'
      direction='row'
      align='center'
      justify='between'
      background='brand'
      pad={{ left: 'medium', right: 'small', vertical: 'small' }}
      elevation='medium'
      style={{ zIndex: '1' }}
      {...props}
  />
);



const App = () => {
  const [showSettings, setShowSettings] = useState(false);
  const [showCustomURL, setShowCustomURL] = useState(false);
  const [isPrivate, setIsPrivate] = useState(false);
  const [showMaxReferences, setShowMaxReferences] = useState(false);
  const [showSidebar, setShowSidebar] = useState(false);
  const [showWissen, setShowWissen] = useState(false);
  const [showProducts, setShowProducts] = useState(false);
  const [showRandomizer, setShowRandomizer] = useState(false);
  const [CustomURL, setCustomURL] = useState('');
  const [MaxReferences, setMaxReferences] = useState(-1);
  const [Randomizer, setRandomizer] = useState(1);
  const [URLInput, setURLInput] = useState('');
  const [myURL, setURL] = useState('');
  const [showResult, setShowResult] = useState(false);
  const [message, setMessage] = useState("");
  const [showHelp, setShowHelp] = useState(false);

  const randomOptions = [
    { label: 'Default', value: 1 },
    { label: 'Uppercase', value: 2 },
    { label: 'Lowercase', value: 3 },
    { label: 'Numbers', value: 4 },
  ];

  useEffect(() => {
    createShortUrl();
  }, [myURL]);

  const createShortUrl = async () => {
    var base = `http://localhost:5000/api/create?url=${myURL}`;
    if(showCustomURL){
      base= base + `&custom=${CustomURL}`;
    }
    if(isPrivate){
      base= base + "&private=1";
    }
    if(showMaxReferences){
      base=base + `&refs_available=${MaxReferences}`;
    }
    if(showRandomizer){
